import React, { FunctionComponent, useState } from "react";
import Event from "../../models/event";

import "./style.css"
import voidevent from "../../models/void-event";
import { Link } from "react-router-dom";
import Cookies from "js-cookie";
import userUpdateFav from "../../api-request/user/update-fav";
import eventUpdate from "../../api-request/event/update";
import getUserById from "../../api-request/user/get-by-id";
import User from "../../models/user";

type Props = {
    event: Event
    clickcard: Function
    clickfav: Function
    CurrentUser: User
};

const EventDetail: FunctionComponent<Props> = ({ event, clickcard, clickfav, CurrentUser }) => {

    var [countfav, setCountfav] = useState<any>(event.favorite);
    var [pseudo, setPseudo] = useState<string>("anonymous");

    var [user, setUser] = useState(CurrentUser);



    const toogleFavorite = (event: Event) => {
        var new_fav: number = countfav
        const token = Cookies.get('token');

        const new_array_fav = user.eventFavorite
        if (!user.eventFavorite.includes(event._id)) {
            new_fav += 1;
            new_array_fav.push(event._id)
        } else {
            new_fav -= 1;
            const pos_id = new_array_fav.indexOf(event._id)
            new_array_fav.splice(pos_id, 1)
        }
        userUpdateFav(new_array_fav, token).then(() => {
            eventUpdate(event._id, event.classe, event.picture, event.color, event.object, new_fav, event.creator, token).then(() => {
                setCountfav(new_fav)
                clickfav(event._id, user._id, new_fav, new_array_fav)
                setUser({ ...user, 'eventFavorite': new_array_fav })
            })
        })
    }

    const handleSelectEvent = () => {
        clickcard(voidevent);
    };

    const copyValue = (test: string) => {
        navigator.clipboard.writeText(test);
    }

    async function getUserNameById() {
        const userData = await getUserById(event.creator);
        setPseudo(userData[0].pseudo);
    }
    getUserNameById()

    return (
        <div className='detail-event p50 flex-row flex-center flex-col-tab flex-start-y-tab'>
            <div className='detail-event__back-button absolute t0 l0 m50'>
                <div className='cta' onClick={handleSelectEvent}>
                    <span>
                        back
                    </span>
                </div>
            </div>


            {event ? (
                <div className='detail-event__container flex-col w80 flex-center-x flex-center-y w95-tab t0'>
                    <div className='detail-event__content flex-row flex-center g50 g10-tab p25 w80 flex-col-tab mt50-tab'>
                        <div className='detail-event__bloc-img flex-center flex-col'>

                            {(event.creator === CurrentUser._id) ? (
                                <div className="detail-event__bloc-img__delete flex-end-y w100 relative">
                                    <i className='detail-event__bloc-img__content__icon-like material-icons absolute' onClick={() => toogleFavorite(event)}>delete</i>
                                </div>
                            ) : null}

                            <img alt='event' className='detail-event__bloc-img__image mb50 mb25-tab' src={event.picture}></img>
                            <div className="detail-event__bloc-img__content flex-row flex-bet w100">
                                <div className="detail-event__bloc-img__content__creator flex-col">
                                    <p className="m0 mb15">Creator :</p>
                                    <Link to={`/user-profile/${event.creator}`} onClick={handleSelectEvent} className="m0 mb15 fs20"><b>{pseudo}</b></Link>
                                </div>
                                <div className="detail-event__bloc-img__content__favorites flex-row flex-start-x g10">
                                    <p className="m0">{countfav}</p>
                                    {user.eventFavorite.includes(event._id) ? (
                                        <i className='detail-event__bloc-img__content__icon-like material-icons user-favorite' onClick={() => toogleFavorite(event)}>favorite_border</i>
                                    ) : (
                                        <i className='detail-event__bloc-img__content__icon-like material-icons' onClick={() => toogleFavorite(event)}>favorite_border</i>
                                    )}
                                </div>
                            </div>
                        </div>
                        <div className='flex-col g10'>
                            <div className='detail-event__bloc-colors g10 flex-col'>
                                <h3 className='detail-event__bloc-colors__title'>Colors : </h3>
                                <div className='detail-event__bloc-colors__color g20 flex-row relative' onClick={() => copyValue(event.color[0])}>
                                    <p className='detail-event__bloc-colors__color__info-value'>Peau :</p>
                                    <div className='detail-event__bloc-colors__color__pastille' style={{ background: '#' + event.color[0] }}></div>
                                    <p className='detail-event__bloc-colors__color__value'>#{event.color[0]}</p>
                                </div>
                                <div className='detail-event__bloc-colors__color g20 flex-row relative' onClick={() => copyValue(event.color[1])}>
                                    <p className='detail-event__bloc-colors__color__info-value'>Cheveux :</p>
                                    <div className='detail-event__bloc-colors__color__pastille' style={{ background: '#' + event.color[1] }}></div>
                                    <p className='detail-event__bloc-colors__color__value'>#{event.color[1]}</p>
                                </div>
                                <div className='detail-event__bloc-colors__color g20 flex-row relative' onClick={() => copyValue(event.color[2])}>
                                    <p className='detail-event__bloc-colors__color__info-value'>Habit 1 :</p>
                                    <div className='detail-event__bloc-colors__color__pastille' style={{ background: '#' + event.color[2] }}></div>
                                    <p className='detail-event__bloc-colors__color__value'>#{event.color[2]}</p>
                                </div>
                                <div className='detail-event__bloc-colors__color g20 flex-row relative' onClick={() => copyValue(event.color[3])}>
                                    <p className='detail-event__bloc-colors__color__info-value'>Habit 2 :</p>
                                    <div className='detail-event__bloc-colors__color__pastille' style={{ background: '#' + event.color[3] }}></div>
                                    <p className='detail-event__bloc-colors__color__value'>#{event.color[3]}</p>
                                </div>
                                <div className='detail-event__bloc-colors__color g20 flex-row relative' onClick={() => copyValue(event.color[4])}>
                                    <p className='detail-event__bloc-colors__color__info-value'>Habit 3 :</p>
                                    <div className='detail-event__bloc-colors__color__pastille' style={{ background: '#' + event.color[4] }}></div>
                                    <p className='detail-event__bloc-colors__color__value'>#{event.color[4]}</p>
                                </div>
                            </div>
                            <div className='detail-event__bloc-object g10 flex-col' >
                                <h3 className='detail-event__bloc-object__title'>Objects : </h3>
                                <div className='detail-event__bloc-objects__object flex-row relative' onClick={() => copyValue(event.object[2])}>
                                    <p className='detail-event__bloc-objects__object__info-value '>Coiffe :</p>
                                    <div className='detail-event__bloc-objects__object__pastille'></div>
                                    <p className='detail-event__bloc-object__object__value'>{event.object[2]}</p>
                                </div>
                                <div className='detail-event__bloc-objects__object flex-row relative' onClick={() => copyValue(event.object[1])}>
                                    <p className='detail-event__bloc-objects__object__info-value'>Cape :</p>
                                    <div className='detail-event__bloc-objects__object__pastille'></div>
                                    <p className='detail-event__bloc-object__object__value'>{event.object[1]}</p>
                                </div>
                                <div className='detail-event__bloc-objects__object flex-row relative' onClick={() => copyValue(event.object[0])}>
                                    <p className='detail-event__bloc-objects__object__info-value'>Bouclier :</p>
                                    <div className='detail-event__bloc-objects__object__pastille'></div>
                                    <p className='detail-event__bloc-object__object__value'>{event.object[0]}</p>
                                </div>
                                <div className='detail-event__bloc-objects__object flex-row relative' onClick={() => copyValue(event.object[4])}>
                                    <p className='detail-event__bloc-objects__object__info-value'>Familier :</p>
                                    <div className='detail-event__bloc-objects__object__pastille'></div>
                                    <p className='detail-event__bloc-object__object__value'>{event.object[4]}</p>
                                </div>
                                {event.object[3] !== "" ? (
                                    <div className='detail-event__bloc-objects__object flex-row relative' onClick={() => copyValue(event.object[3])}>
                                        <p className='detail-event__bloc-objects__object__info-value'>Costume :</p>
                                        <div className='detail-event__bloc-objects__object__pastille'></div>
                                        <p className='detail-event__bloc-object__object__value'>{event.object[3]}</p>
                                    </div>
                                ) : null}
                            </div>
                        </div>
                    </div>
                </div>
            ) : (
                <div className='detail-event__not-found'>
                    <h2 className='detail-event__not-found__title'>No event find</h2>
                </div>
            )}
            <div className='detail-event__info absolute b0 r0 pr20 relative-tab mt25-tab'>
                <p className='detail-event__info_link'>image venant de <a href='https://www.dofusbook.net/' target='new_blank'>dofusbook.net</a></p>
            </div>
        </div>
    )
}

export default EventDetail;
