import React, { FunctionComponent, useState } from "react";
import Skin from "../../models/skin";

import getUserById from "../../api-request/user/get-by-id";
import "./style.css"
import voidskin from "../../models/void-skin";
import { Link } from "react-router-dom";
import skinUpdate from "../../api-request/skin/update";
import Cookies from "js-cookie";
import getCurrent from "../../api-request/user/get-current";
import userUpdate from "../../api-request/user/update";

type Props = {
    skin: Skin
    clickcard: Function
};

const SkinDetail: FunctionComponent<Props> = ({ skin, clickcard }) => {

    var [countfav, setCountfav] = useState<any>(skin.favorite);
    var [pseudo, setPseudo] = useState("anonymous");


    const toogleFavorite = (skin: Skin) => {
        var new_fav: number = skin.favorite
        const token = Cookies.get('token');
        getCurrent(token).then(result => {

            const new_array_fav = result[0].skinFavorite
            if (!result[0].skinFavorite.includes(skin._id)) {
                new_fav += 1;
                new_array_fav.push(skin._id)
            } else {
                new_fav -= 1;
                const pos_id = new_array_fav.indexOf(skin._id)
                new_array_fav.splice(pos_id, 1)
            }
            userUpdate(result[0]._id, result[0].pseudo, result[0].email, result[0].password, result[0].role, result[0].skinOwner, new_array_fav, token).then(() => {
                skinUpdate(skin._id, skin.classe, skin.picture, skin.color, skin.object, new_fav, skin.creator, token).then(() => {
                    setCountfav(new_fav)
                });
            }
            );
        })

    }

    const handleSelectSkin = () => {
        clickcard(voidskin);
    };

    const copyValue = (test: string) => {
        navigator.clipboard.writeText(test);
    }

    async function getUserNameById() {
        const userData = await getUserById(skin.creator);
        setPseudo(userData[0].pseudo);
    }
    getUserNameById()

    return (
        <div className='detail-skin p50 flex-row flex-center flex-col-tab flex-start-y-tab'>
            <div className='detail-skin__back-button absolute t0 l0 m50'>
                <div className='cta' onClick={handleSelectSkin}>
                    <span>
                        back
                    </span>
                </div>
            </div>


            {skin ? (
                <div className='detail-skin__container flex-col w80 flex-center-x flex-center-y w95-tab t0'>
                    <div className='detail-skin__content flex-row flex-center g50 g10-tab p25 w80 flex-col-tab mt50-tab'>
                        <div className='detail-skin__bloc-img flex-center flex-col'>
                            <img alt='skin' className='detail-skin__bloc-img__image mb50 mb25-tab' src={skin.picture}></img>
                            <div className="detail-skin__bloc-img__content flex-row flex-bet w100">
                                <div className="detail-skin__bloc-img__content__creator flex-col">
                                    <p className="m0 mb15">Creator :</p>
                                    <Link to={`/user-profile/${skin.creator}`} className="m0 mb15 fs20"><b>{pseudo}</b></Link>
                                </div>
                                <div className="detail-skin__bloc-img__content__favorites flex-row flex-start-x g10">
                                    <p className="m0">{countfav}</p>
                                    {countfav > 0 ? (
                                        <i className='detail-skin__bloc-img__content__icon-like material-icons user-favorite' onClick={() => setCountfav(toogleFavorite(skin))}>favorite_border</i>
                                    ) : (
                                        <i className='detail-skin__bloc-img__content__icon-like material-icons' onClick={() => toogleFavorite(skin)}>favorite_border</i>
                                    )}
                                </div>
                            </div>
                        </div>
                        <div className='flex-col g10'>
                            <div className='detail-skin__bloc-colors g10 flex-col'>
                                <h3 className='detail-skin__bloc-colors__title'>Colors : </h3>
                                <div className='detail-skin__bloc-colors__color g20 flex-row relative' onClick={() => copyValue(skin.color[0])}>
                                    <p className='detail-skin__bloc-colors__color__info-value'>Peau :</p>
                                    <div className='detail-skin__bloc-colors__color__pastille' style={{ background: '#' + skin.color[0] }}></div>
                                    <p className='detail-skin__bloc-colors__color__value'>#{skin.color[0]}</p>
                                </div>
                                <div className='detail-skin__bloc-colors__color g20 flex-row relative' onClick={() => copyValue(skin.color[1])}>
