import React, { FunctionComponent, useEffect, useState } from 'react';
import Event from '../models/event';
import User from '../models/user';
import { Link, RouteComponentProps } from 'react-router-dom';
import EventForm from '../components/event-form';
import Cookies from 'js-cookie';
import voidevent from '../models/void-event';

interface Props extends RouteComponentProps<{ id: string }> {
    CurrentUser: User
    eventList: Array<Event>,
}

const EventEdit: FunctionComponent<Props> = ({ match, CurrentUser, eventList }) => {

    const [event, setEvent] = useState<Event>(voidevent );

    useEffect(() => {
        eventList.forEach((event: Event) => {
            if (match.params.id === event._id) {
                setEvent(event)
            }
        });

    }, [match.params.id, eventList, CurrentUser]);

    const requestEdit = (newEventPart: any) => {
        const newEvent: Event = { ...newEventPart, creator: CurrentUser._id }

        const token = Cookies.get('token')
        if (token) {
/*             eventEdit(newEvent.name, newEvent.picture, newEvent.maxPlace, newEvent.dispoPlace, newEvent.usersReserve, token).then(eventResult => {
                delete eventResult[0].__v
                var new_array_crea: Array<string> = user.eventOwner
                new_array_crea.push(eventResult[0]._id)
                userUpdateOwner(new_array_crea, token).then(() => {
                    clickEventOwner(user._id, new_array_crea, eventResult[0])
                    history.push(`../user-profile/${user._id}`)
                })
            }) */
        }
    }

    return (
        <div className='event-add p50 flex-row flex-center relative'>

            <div className='event-add__back-button absolute t0 l0 m50'>
                <Link to={'/user-profile/'+CurrentUser._id} className='cta '>
                    <span>
                        back
                    </span>
                </Link>
            </div>

            <EventForm request={requestEdit} event={event} />
        </div>
    )
}


export default EventEdit;
