import React, { FunctionComponent, useEffect, useState } from "react";
import Event from "../../models/event";
import "./style.css"
import Cookies from "js-cookie";
import getCurrent from "../../api-request/user/get-current";
import userUpdateFav from "../../api-request/user/update-fav";
import eventUpdate from "../../api-request/event/update";
import User from "../../models/user";

type Props = {
    event: Event
    clickcard: Function
    clickfav: Function
    CurrentUser: User
};

const EventCard: FunctionComponent<Props> = ({ event, CurrentUser, clickcard, clickfav }) => {

    var [countfav, setCountfav] = useState(event.favorite);
    var [user, setUser] = useState(CurrentUser);


    const toogleFavorite = (event: Event) => {
        var new_fav: number = countfav
        const token = Cookies.get('token');

        const new_array_fav = user.eventFavorite
        if (!user.eventFavorite.includes(event._id)) {
            new_fav += 1;
            new_array_fav.push(event._id)
        } else {
            new_fav -= 1;
            const pos_id = new_array_fav.indexOf(event._id)
            new_array_fav.splice(pos_id, 1)
        }
        userUpdateFav(new_array_fav, token).then(() => {
            eventUpdate(event._id, event.classe, event.picture, event.color, event.object, new_fav, event.creator, token).then(() => {
                setCountfav(new_fav)
                clickfav(event._id, user._id, new_fav, new_array_fav)
                setUser({ ...user, 'eventFavorite': new_array_fav })
            })
        })
    }

    useEffect(() => {
        setCountfav(event.favorite)
        setUser(CurrentUser)
    }, [event, CurrentUser])



    const handleSelectEvent = () => {
        clickcard(event);
    };




    return (
        <li key={event._id} className='card-event flex-row flex-center-y p15 relative'>
            <img alt='event' className="card-event__item" src={event.picture} onClick={handleSelectEvent}></img>
            {user.eventFavorite.includes(event._id) ? (
                <div className='card-event__favorite absolute b5 r5 flex-center user-favorite'>
                    <div>{countfav}</div>
                    <i className='material-icons' onClick={() => toogleFavorite(event)}>favorite_border</i>
                </div>
            ) : (

                <div className='card-event__favorite absolute b5 r5 flex-center'>
                    <div>{countfav}</div>
                    <i className='material-icons' onClick={() => toogleFavorite(event)}>favorite_border</i>
                </div>
            )}
        </li>
    );

}

export default EventCard;
