import React, { FunctionComponent, useState, useEffect } from 'react';
import { RouteComponentProps, Link } from 'react-router-dom';
import Skin from '../models/skin';
import SKINS from '../models/mock-skins';
import formatDate from '../helpers/format-date';

type Params = { id: string };

const SkinDetail: FunctionComponent<RouteComponentProps<Params>> = ({ match }) => {

    const [skin, setSkin] = useState<Skin | null>(null);

    useEffect(() => {
        SKINS.forEach(skin => {
            if (match.params.id === skin.id.toString()) {
                setSkin(skin);
            }
        })
    }, [match.params.id]);

    return (
        <div className='p50 flex-col'>
            <Link to="/skins" className='cta w10'>
                <span>
                    back
                </span>
            </Link>
            {skin ? (
                <div className='flex-row w50'>

                    <div className='flex-col'>
                        <p>{skin.classe}</p>
                        <img src={skin.picture}></img>
                        <p>{formatDate(skin.created)}</p>
                    </div>


                    <div className='flex-col flex-bet pt25 pb25'>
                        <div>
                            {skin.color.map(color => (
                                <div className="flex-row flex-center-x" key={color}>
                                    <div className="pastille mr10" style={{ background: color }}></div>
                                    <p>{color}</p>
                                </div>
                            ))}
                        </div>
                        <div>
                            {skin.object.map(obj => (
                                <p key={obj}>{obj}</p>
                            ))}
                        </div>

                    </div>

                </div>

            ) : (
                <h2>Aucuns Skin trouv√©</h2>
            )}
        </div>
    )
}

export default SkinDetail;
