import React, { FunctionComponent, useEffect, useState } from "react";
import Skin from "../../models/skin";
import "./style.css"
import Cookies from "js-cookie";
import getCurrent from "../../api-request/user/get-current";
import userUpdateFav from "../../api-request/user/update-fav";
import skinUpdate from "../../api-request/skin/update";
import User from "../../models/user";

type Props = {
    skin: Skin
    clickcard: Function
    clickfav: Function
    CurrentUser:User
};

const SkinCard: FunctionComponent<Props> = ({ skin, CurrentUser, clickcard, clickfav }) => {

    var [countfav, setCountfav] = useState(skin.favorite);
    var [User, setUser] = useState(CurrentUser);
    
    
    const toogleFavorite = (skin: Skin) => {
        var new_fav: number = countfav
        const token = Cookies.get('token');
        getCurrent(token).then(result => {

            const new_array_fav = User.skinFavorite
            if (!User.skinFavorite.includes(skin._id)) {
                new_fav += 1;
                new_array_fav.push(skin._id)
            } else {
                new_fav -= 1;
                const pos_id = new_array_fav.indexOf(skin._id)
                new_array_fav.splice(pos_id, 1)
            }
            userUpdateFav(new_array_fav, token).then(() => {
                skinUpdate(skin._id, skin.classe, skin.picture, skin.color, skin.object, new_fav, skin.creator, token).then(() => {
                    setCountfav(new_fav)
                    clickfav(skin._id, User._id, new_fav, new_array_fav)
                    setUser({ ...User, 'skinFavorite': new_array_fav })
                })
            })
        })
    }

    useEffect( () => {
        setCountfav(skin.favorite)
        setUser(CurrentUser)
    }, [skin, CurrentUser])



    const handleSelectSkin = () => {
        clickcard(skin);
      };




        return (
            <li key={skin._id} className='card-skin flex-row flex-center-y p15 relative'>
                <img alt='skin' className="card-skin__item" src={skin.picture} onClick={handleSelectSkin}></img>
                {User.skinFavorite.includes(skin._id) ? (
                    <div className='card-skin__favorite absolute b5 r5 flex-center user-favorite'>
                        <div>{countfav}</div>
                        <i className='material-icons' onClick={() => toogleFavorite(skin)}>favorite_border</i>
                    </div>
                ) : (

                    <div className='card-skin__favorite absolute b5 r5 flex-center'>
                        <div>{countfav}</div>
                        <i className='material-icons' onClick={() => toogleFavorite(skin)}>favorite_border</i>
                    </div>
                )}
            </li>
        );

}

export default SkinCard;
