import React, { FunctionComponent } from "react";
import Skin from "../../models/skin";
import "./card.css"
import formatDate from "../../helpers/format-date";

type Props = {
    skin: Skin
};

const SkinCard: FunctionComponent<Props> = ({ skin }) => {



    return (
        <li className='flex-row w30 flex-center-y p15 relative'>
            <div className='flex-col'>
                <h3 className='txt-left mt0'>classe : {skin.classe}</h3>
                <img src={skin.picture}></img>
                <p className="absolute b0">{formatDate(skin.created)}</p>
            </div>
            <div className='flex-col flex-bet pt25 pb25'>
                <div>
                    {skin.color.map(color => (
                        <div className="flex-row flex-center-x"  key={color}>
                            <div className="pastille mr10" style={{ background: color }}></div>
                            <p>{color}</p>
                        </div>
                    ))}
                </div>
                <div>
                    {skin.object.map(obj => (
                        <p key={obj}>{obj}</p>
                    ))}
                </div>

            </div>
        </li>
    );
}

export default SkinCard;
