import React, { FunctionComponent, useState, useEffect } from 'react';
import Skin from '../../models/skin';
import SkinCard from '../skin-card';
import { Link } from 'react-router-dom';
import './style.css'

type Props = {
    SkinsIn: Array<Skin>
    DomClass: String
    clickcard: Function
    clickfav: Function
    CurrentUserLike : Array<string>
};

const skinsListInput: FunctionComponent<Props> = ({ SkinsIn, DomClass, clickcard, clickfav, CurrentUserLike}) => {



    // eslint-disable-next-line react-hooks/rules-of-hooks
    const [skins, setSkins] = useState(SkinsIn);


    // eslint-disable-next-line react-hooks/rules-of-hooks
    useEffect(() => {
        setSkins(SkinsIn);
    }, [SkinsIn]);

    const handleKeyDown = (event: { key: string; }) => {
        if (event.key === 'Enter') {
            prefilter();
        }
    }

    const filter = (classe: string, item: string) => {
        var filteredSkins = SkinsIn
        if (classe !== '') {
            if (classe === 'all') {
                setSkins(SkinsIn);
            }
            else {
                filteredSkins = filteredSkins.filter(skin => skin.classe === classe);
                setSkins(filteredSkins);
            }
        }
        if (item !== '') {
            filteredSkins = filteredSkins.filter(skin => skin.object.includes(item) || skin.classe === (item));
            setSkins(filteredSkins);
        }
        checkfilter(filteredSkins)
    }


    const checkfilter = (filteredSkins: object) => {
        if (SkinsIn !== filteredSkins) {
            document.querySelector('.' + DomClass +' .skin-list__search__reset')!.classList.remove('hide')
        }
        else {
            document.querySelector('.' + DomClass +' .skin-list__search__reset')!.classList.add('hide')
        }
    }

    const prefilter = () => {

        var classe = 'all'
        var item = ''
        if (document.querySelector('.' + DomClass +' .active-filter')) {
            classe = document.querySelector('.' + DomClass +' .active-filter')!.id.slice(7);
        }
        if ((document.querySelector('.' + DomClass +' .skin-list__search__option__bar') as HTMLInputElement).value !== '') {
            item = (document.querySelector('.' + DomClass +' .skin-list__search__option__bar') as HTMLInputElement).value;
        }
        filter(classe, item)
    }


    const classesFilter = (classe: string) => {
        const test = document.querySelector('#filter-' + classe +'.' + DomClass);
        if (test!.classList.value.includes('active-filter')) {
            test!.classList.toggle('active-filter');
            prefilter();
        } else {
            if (document.querySelector('.' + DomClass +' .active-filter')) {
                document.querySelector('.' + DomClass +' .active-filter')!.classList.remove('active-filter')
            }
            test!.classList.toggle('active-filter');
            prefilter();
        }
    }

    const resetfilter = () => {
        if (document.querySelector('.' + DomClass +' .active-filter')) {
            document.querySelector('.' + DomClass +' .active-filter')!.classList.remove('active-filter');
        }
        (document.querySelector('.' + DomClass +' .skin-list__search__option__bar') as HTMLInputElement).value = '';
        filter('all', '')
        document.querySelector('.' + DomClass +' .skin-list__search__reset')!.classList.add('hide')

    }

    const classList: string[] = [];

    SkinsIn.forEach(element => {
        if (!classList.includes(element.classe)) {
            classList.push(element.classe)
        }
    });

    return (


        // eslint-disable-next-line no-useless-concat
        <div className={'skin-list flex-center-x flex-col pl50 pr50 ' + DomClass} >
            <div className='skin-list__search w75 pt50 flex-col'>

                <div className='skin-list__search__option flex-row g10 flex-center pl50 mb20 pr50'>
                    <input id='search-bar' type='text' className='skin-list__search__option__bar w100 fs16' placeholder='Search user, classes, item ...' onKeyDown={handleKeyDown} />
                    <div className='skin-list__search__option__button flex-center'>
                        <i className='material-icons' onClick={() => prefilter()}>search</i>
                    </div>
                </div>
                <div className='skin-list__classes-filter flex-wrap pl50 pr50 none-tab'>
                    {classList.map(classe => (
                        <div key={classe} id={'filter-' + classe} className={DomClass + ' skin-list__classes-filter__classe p10 pl15 pr15 fs16 mr15 mb5'} onClick={() => classesFilter(classe)}>{classe}</div>
                    ))}
                </div>

                <div className='skin-list__search__reset mt20 ml50 hide'>
                    <div className='cta' onClick={() => resetfilter()}>
                        <span>
                            reset filters
                        </span>
                    </div>
                </div>
            </div>


            <ul className='skin-list__container flex-wrap w100-tab w80 g15 pb50 flex-center'>
                {skins.map(skin => (
                    <SkinCard 
                    key={skin._id} 
                    skin={skin} 
                    CurrentUserLike={CurrentUserLike} 
                    clickcard={clickcard}
                    clickfav={clickfav}
                    />
                ))}
                <li className='card-skin__create flex-row flex-center-y relative'>
                    <Link to='/skins/add'>
                        <img className='card-skin__create__item' src='https://media.discordapp.net/attachments/967347575390863360/1097519818581737542/image.png' alt='skin'></img>
                    </Link>
                </li>
            </ul>


        </div>
    );
}

export default skinsListInput;
