import React, { FunctionComponent, useState } from "react";
import Skin from "../../models/skin";

import getUserById from "../../api-request/user/get-user-by-_id";
import "./style.css"
import voidskin from "../../models/void-skin";
import { Link } from "react-router-dom";

type Props = {
    skin: Skin
    clickcard: Function
};

const SkinDetail: FunctionComponent<Props> = ({ skin, clickcard }) => {

    var [countfav, setCountfav] = useState(skin.favorite);
    var [pseudo, setPseudo] = useState("anonymous");


    const toogleFavorite = (id: string, type: string) => {
        if (type === "add") {
            console.log("adding favorite");
            countfav += 1
            setCountfav(countfav) //! patch when back done
        }
        else if (type === "remove") {
            console.log("removing favorite");
            countfav -= 1
            setCountfav(countfav) //! patch when back done
        }
    }

    const handleSelectSkin = () => {
        clickcard(voidskin);
    };

    const copyValue = (test: string) => {
        navigator.clipboard.writeText(test);
    }

    async function getUserNameById() {
        ;
        const userData = await getUserById(skin.creator);
        setPseudo(userData[0].pseudo);
    }
    getUserNameById()
    console.log('ok')

    return (
        <div className='detail-skin p50 flex-row flex-center flex-col-tab flex-start-y-tab'>
            <div className='detail-skin__back-button absolute t0 l0 m50'>
                <div className='cta' onClick={handleSelectSkin}>
                    <span>
                        back
                    </span>
                </div>
            </div>


            {skin ? (
                <div className='detail-skin__container flex-col w80 flex-center-x flex-center-y w95-tab t0'>
                    <div className='detail-skin__content flex-row flex-center g50 g10-tab p25 w80 flex-col-tab mt50-tab'>
                        <div className='detail-skin__bloc-img flex-center flex-col'>
                            <img alt='skin' className='detail-skin__bloc-img__image mb50 mb25-tab' src={skin.picture}></img>
                            <div className="detail-skin__bloc-img__content flex-row flex-bet w100">
                                <div className="detail-skin__bloc-img__content__creator flex-col">
                                    <p className="m0 mb15">Creator :</p>
                                    <Link to={`/user-profile/${skin.creator}`} className="m0 mb15 fs20"><b>{pseudo}</b></Link>
                                </div>
                                <div className="detail-skin__bloc-img__content__favorites flex-row flex-start-x g10">
                                    <p className="m0">{countfav}</p>
                                    {countfav > 0 ? (
                                        <i className='detail-skin__bloc-img__content__icon-like material-icons user-favorite' onClick={() => toogleFavorite(skin._id, 'remove')}>favorite_border</i>
                                    ) : (
                                        <i className='detail-skin__bloc-img__content__icon-like material-icons' onClick={() => toogleFavorite(skin._id, 'add')}>favorite_border</i>
                                    )}
                                </div>
                            </div>
                        </div>
                        <div className='flex-col g10'>
                            <div className='detail-skin__bloc-colors g10 flex-col'>
                                <h3 className='detail-skin__bloc-colors__title'>Colors : </h3>
                                <div className='detail-skin__bloc-colors__color g20 flex-row relative' onClick={() => copyValue(skin.color[0])}>
                                    <p className='detail-skin__bloc-colors__color__info-value'>Peau :</p>
                                    <div className='detail-skin__bloc-colors__color__pastille' style={{ background: '#' + skin.color[0] }}></div>
                                    <p className='detail-skin__bloc-colors__color__value'>#{skin.color[0]}</p>
                                </div>
                                <div className='detail-skin__bloc-colors__color g20 flex-row relative' onClick={() => copyValue(skin.color[1])}>
                                    <p className='detail-skin__bloc-colors__color__info-value'>Cheveux :</p>
                                    <div className='detail-skin__bloc-colors__color__pastille' style={{ background: '#' + skin.color[1] }}></div>
                                    <p className='detail-skin__bloc-colors__color__value'>#{skin.color[1]}</p>
                                </div>
                                <div className='detail-skin__bloc-colors__color g20 flex-row relative' onClick={() => copyValue(skin.color[2])}>
                                    <p className='detail-skin__bloc-colors__color__info-value'>Habit 1 :</p>
                                    <div className='detail-skin__bloc-colors__color__pastille' style={{ background: '#' + skin.color[2] }}></div>
                                    <p className='detail-skin__bloc-colors__color__value'>#{skin.color[2]}</p>
                                </div>
                                <div className='detail-skin__bloc-colors__color g20 flex-row relative' onClick={() => copyValue(skin.color[3])}>
                                    <p className='detail-skin__bloc-colors__color__info-value'>Habit 2 :</p>
                                    <div className='detail-skin__bloc-colors__color__pastille' style={{ background: '#' + skin.color[3] }}></div>
                                    <p className='detail-skin__bloc-colors__color__value'>#{skin.color[3]}</p>
                                </div>
                                <div className='detail-skin__bloc-colors__color g20 flex-row relative' onClick={() => copyValue(skin.color[4])}>
                                    <p className='detail-skin__bloc-colors__color__info-value'>Habit 3 :</p>
                                    <div className='detail-skin__bloc-colors__color__pastille' style={{ background: '#' + skin.color[4] }}></div>
                                    <p className='detail-skin__bloc-colors__color__value'>#{skin.color[4]}</p>
                                </div>
                            </div>
                            <div className='detail-skin__bloc-object g10 flex-col' >
                                <h3 className='detail-skin__bloc-object__title'>Objects : </h3>
                                <div className='detail-skin__bloc-objects__object flex-row relative' onClick={() => copyValue(skin.object[2])}>
                                    <p className='detail-skin__bloc-objects__object__info-value '>Coiffe :</p>
                                    <div className='detail-skin__bloc-objects__object__pastille'></div>
                                    <p className='detail-skin__bloc-object__object__value'>{skin.object[2]}</p>
                                </div>
                                <div className='detail-skin__bloc-objects__object flex-row relative' onClick={() => copyValue(skin.object[1])}>
                                    <p className='detail-skin__bloc-objects__object__info-value'>Cape :</p>
                                    <div className='detail-skin__bloc-objects__object__pastille'></div>
                                    <p className='detail-skin__bloc-object__object__value'>{skin.object[1]}</p>
                                </div>
                                <div className='detail-skin__bloc-objects__object flex-row relative' onClick={() => copyValue(skin.object[0])}>
                                    <p className='detail-skin__bloc-objects__object__info-value'>Bouclier :</p>
                                    <div className='detail-skin__bloc-objects__object__pastille'></div>
                                    <p className='detail-skin__bloc-object__object__value'>{skin.object[0]}</p>
                                </div>
                                <div className='detail-skin__bloc-objects__object flex-row relative' onClick={() => copyValue(skin.object[4])}>
                                    <p className='detail-skin__bloc-objects__object__info-value'>Familier :</p>
                                    <div className='detail-skin__bloc-objects__object__pastille'></div>
                                    <p className='detail-skin__bloc-object__object__value'>{skin.object[4]}</p>
                                </div>
                                {skin.object[3] !== "" ? (
                                    <div className='detail-skin__bloc-objects__object flex-row relative' onClick={() => copyValue(skin.object[3])}>
                                        <p className='detail-skin__bloc-objects__object__info-value'>Costume :</p>
                                        <div className='detail-skin__bloc-objects__object__pastille'></div>
                                        <p className='detail-skin__bloc-object__object__value'>{skin.object[3]}</p>
                                    </div>
                                ) : null}
                            </div>
                        </div>
                    </div>
                </div>
            ) : (
                <div className='detail-skin__not-found'>
                    <h2 className='detail-skin__not-found__title'>No skin find</h2>
                </div>
            )}
            <div className='detail-skin__info absolute b0 r0 pr20 relative-tab mt25-tab'>
                <p className='detail-skin__info_link'>image venant de <a href='https://www.dofusbook.net/' target='new_blank'>dofusbook.net</a></p>
            </div>
        </div>
    )
}

export default SkinDetail;
