async function userUpdate(_id: string, pseudo: string, email: string, skinOwner: Array<string>, skinFavorite: Array<string>, role: string, password: string, token:string | undefined) {
    var data: any = []
    const response = await fetch("http://localhost:4000/skins/" + _id, {
        method: "PUT",
        body: JSON.stringify({_id, pseudo, email, password, role, skinOwner, skinFavorite}),
        headers: { "Content-Type": "application/json", "authorization" : "Bearer " + token}
    });
    data = [await response.status];
    return data
}

export default userUpdate;
