import React, { FunctionComponent, useEffect, useState } from 'react';
import { Link, RouteComponentProps } from 'react-router-dom';
import SkinsListInput from '../components/skin-list';
import SkinDetail from '../components/skin-detail';
import Skin from "../models/skin";
import voidskin from '../models/void-skin';
import User from '../models/user';
import voiduser from '../models/void-user';


interface Props extends RouteComponentProps<{ id: string }> {
    skinList: Array<Skin>,
    userList: Array<User>,
    clickfav: Function
}

const UserProfile: FunctionComponent<Props> = ({ match, skinList, userList, clickfav }) => {


    const [user, setUser] = useState<User>(voiduser);

    useEffect(() => {
        userList.forEach((user: User) => {
            if (match.params.id === user._id) {
                setUser(user)
            }
        })
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [match.params.id]);

    const [detailskin, setDetailskin] = useState(voidskin);

    const openDetail = (skinfrom: Skin) => {
        setDetailskin(skinfrom)
    }

    const [skinFaved, setSkinFaved] = useState([voidskin]);
    const [skinOwner, setSkinOwner] = useState([voidskin]);

    useEffect(() => {
        const newSkinFaved: any = []
        // eslint-disable-next-line array-callback-return
        skinList.forEach((skin: Skin) => {
            if (user.skinFavorite.includes(skin._id)) {
                newSkinFaved.push(skin)
            }
        })


        const newSkinOwner: any = []
        // eslint-disable-next-line array-callback-return
        skinList.forEach((skin: Skin) => {
            if (user.skinOwner.includes(skin._id)) {
                newSkinOwner.push(skin)
            }
        })
        setSkinfaved(newSkinFaved)
        setSkinOwner(newSkinOwner)
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [skinList, userList]);





    const switchUserList = (type: string) => {
        var domElement = document.querySelector('.user-page__container')!
        if (type === 'fav') {
            if (!domElement!.classList.value.includes(type)) {
                domElement.classList.remove('crea');
                domElement.classList.add('fav');
            }
        }

        if (type === 'crea') {
            if (!domElement.classList.value.includes(type)) {
                domElement.classList.add('crea');
                domElement.classList.remove('fav');
            }
        }
    }


    return (
        <div className='user-page flex-start-x flex-center-y flex-row p50 pb0 relative flex-col-tab'>
            <div className="user-page__user-info flex-col w25 w100-tab">
                <div className='user-page__user-info__container flex-center-x flex-col p10 relative'>
                    <div className="user-page__user-info__container__main-bg absolute w95 h25"></div>
                    <img className='user-page__user-info__container__main-img mt35' src="https://media.discordapp.net/attachments/967347575390863360/1097526768572846110/pict_profile.png?width=671&height=671" alt="" width={"80%"} />
                    <h2 className='user-page__user-info__container__main-name mb50'>{user.pseudo}</h2>

                    <div className='user-page__user-info__container__stats flex-row w90 relative'>
                        <div className='user-page__user-info__container__stats__created flex-col w50 flex-center'>
                            <span className='mt5 mb15 flex-center fs20' onClick={() => switchUserList('crea')}>45</span>
                        </div>
                        <div className='user-page__user-info__container__stats__liked flex-col w50 flex-center'>
                            <span className='mt5 mb15 flex-center fs20'>45</span>
                        </div>
                    </div>
                    <div className='flex-col absolute b0 m15 relative-tab' >
                        <p className='mt5 mb5'>member since :</p>
                        <p className='mt5 mb15'>25 . 12 . 2022</p>
                    </div>
                </div>
            </div>
            <div className="user-page__container flex-col w75 w100-tab flex-start-x flex-start-y crea" >


                <div className='user-page__container__titles-content zi2 flex-row w100 flex-around h10 relative flex-col-tab'>
                    <div className='flex-row'>

                        <h2 className='user-page__container__titles-content__title__crea upper p25 m0 fs18-tab' onClick={() => switchUserList('crea')}><span>Skins creations</span></h2>
                        <h2 className='user-page__container__titles-content__title__fav upper p25 m0 fs18-tab' onClick={() => switchUserList('fav')}><span>Skins liked</span></h2>
                    </div>

                    <div className='relative mtbauto'>
                        <Link to='/skins' className="cta mb35">
                            <span>
                                See all skins
                            </span>
                        </Link>
                    </div>
                </div>
                <div className='user-page__container__contents flex-row flex-around h10'>
                    <div className="user-page__container__content__creations w50 h90">

                        <SkinsListInput
                            SkinsIn={skinOwner}
                            DomClass={"crea-skin-user"}
                            clickcard={openDetail}
                            clickfav={clickfav}
                        />

                    </div>
                    <div className="user-page__container__content__favorites w50 h90">

                        <SkinsListInput
                            SkinsIn={skinFaved}
                            DomClass={"fav-skin-user"}
                            clickcard={openDetail}
                            clickfav={clickfav}
                        />

                    </div>
                </div>

            </div>

            {detailskin.classe !== "no-display" ? (
                <SkinDetail
                    skin={detailskin}
                    clickcard={openDetail}
                    clickfav={clickfav}
                />
            ) : null}

        </div>
    );
}

export default UserProfile;
