import React, { FunctionComponent, useState, useEffect } from 'react';
import Skin from '../models/skin';
import SKINS from '../models/mock-skins';
import SkinCard from '../components/skin-card';
import { Link } from 'react-router-dom';

const SkinList: FunctionComponent = () => {
    const [skins, setSkins] = useState<Skin[]>(SKINS);

    useEffect(() => {
        setSkins(SKINS);
    }, []);


    const filter = (classe: string, item:string, color:string) => {
        var filteredSkins = SKINS
        if (classe !== '') {
            if (classe === 'all') {
                setSkins(SKINS);
            }
            else {
                filteredSkins = filteredSkins.filter(skin => skin.classe === classe);
                setSkins(filteredSkins);
            }
        }
        if (item !== '') {
            filteredSkins = filteredSkins.filter(skin => skin.object.includes(item));
        }
        
        if (color !== '') {
            filteredSkins = filteredSkins.filter(skin => skin.color.includes(color));
        }
    }


    const classesFilter = (classe: string) => {
        const test = document.getElementById("filter-" + classe);
        if (test!.classList.value.includes("active-filter")) {
            filter('all', '', '');
        } else {
            filter(classe, '', '');
            if (document.querySelector(".active-filter")) {
                document.querySelector(".active-filter")!.classList.remove("active-filter")
            }
        }
        test!.classList.toggle("active-filter");
    }




    const classList: string[] = [
        'Iop', 'Cra', 'Feca', 'Sram', 'Eniripsa', 'Enutrof', 'Ecaflip', 'Osamodas', 'Sacrieur', 'Xelor', 'Sadida', 'Pandawa', 'Zobal', 'Roublard', 'Steamer', 'Eliotrope', 'Ouginak', 'Forgelance'
    ];



    return (

        <div className="skin-list flex-center-x flex-col pl50 pr50 ">

            <div className="skin-list__search w75 pt50 flex-col">

                <div className="skin-list__search__option w100 flex-row g25 flex-center pl50 mb20">
                    <input id="search-bar" type="text" className="skin-list__search__option__bar w100 fs16" placeholder="Search user, classes, item ..." />
                    <input id="search-color" type="color" className="skin-list__search__option__color" defaultValue={'#3b4350'} />
                    <div className="skin-list__search__option__button flex-center">
                        <i className='material-icons'>search</i>
                    </div>
                </div>
                <div className='skin-list__classes-filter flex-wrap w100 pl50 '>
                    {classList.map(classe => (
                        <div id={"filter-" + classe} className='skin-list__classes-filter__classe p10 pl15 pr15 fs16 mr15 mb5' onClick={() => classesFilter(classe)}>{classe}</div>
                    ))}
                </div>
            </div>


            <h1 className='skin-list__team__title w75 mt50'>All skins idea :</h1>
            <ul className='skin-list__container flex-wrap w100-tab w80 g15 pb50'>
                {skins.map(skin => (
                    <SkinCard key={skin.id} skin={skin} exept={[]} />
                ))}
                <li className='card-skin__create flex-row flex-center-y p15 relative'>
                    <Link to="/skins/add">
                        <img className="card-skin__create__item" src="https://media.discordapp.net/attachments/1090272264810942504/1090276175294759095/308_400.png" alt='skin'></img>
                    </Link>
                </li>
            </ul>
        </div>
    );
}

export default SkinList;
