import React, { FunctionComponent } from 'react';
import { Link } from 'react-router-dom';
import SKINS from '../models/mock-skins';
import SkinListInput from '../components/skin-list';
import Skin from '../models/skin';

const UserProfile: FunctionComponent = () => {

    const skinfaved: any = []
    // eslint-disable-next-line array-callback-return
    SKINS.forEach(skin => {
        if (skin.favorite !== 0) {
            skinfaved.push(skin)
        }
    })

    const switchUserList = (type: string) => {
        var domElement = document.querySelector('.user-page__container')!
        if (type === 'fav') {
            if (!domElement!.classList.value.includes(type)) {
                domElement.classList.remove('crea');
                domElement.classList.add('fav');
            }
        }

        if (type === 'crea') {
            if (!domElement.classList.value.includes(type)) {
                domElement.classList.add('crea');
                domElement.classList.remove('fav');
            }
        }
    }


    return (
        <div className='user-page flex-start-x flex-center-y flex-row p50 pb0 relative'>
            <div className="user-page__user-info flex-col w25">
                <div className='user-page__user-info__container flex-center-x flex-col p10 relative'>
                    <img className='mt35' src="https://media.discordapp.net/attachments/967347575390863360/1097526768572846110/pict_profile.png?width=671&height=671" alt="" width={"80%"} />
                    <h2 className='mb50'>pseudo</h2>

                    <div className='flex-row w100'>
                        <div className='flex-col w50 flex-center'>
                            <div className='mt5 mb15'>45</div>
                        </div>
                        <div className='flex-col w50 flex-center'>
                            <div className='mt5 mb15'>45</div>
                        </div>
                    </div>
                        <div className='flex-row g10 absolute b0' >
                            <p className='mt5 mb5'>member since :</p>
                            <p className='mt5 mb15'>25 . 12 . 2022</p>
                        </div>
                </div>
            </div>
            <div className="user-page__container flex-col w75 flex-start-x flex-start-y crea" >


                <div className='user-page__container__titles-content zi2 flex-row w100 flex-around h10 relative'>
                    <h2 className='user-page__container__titles-content__crea upper p25 m0' onClick={() => switchUserList('crea')}><span>Skins creations</span></h2>
                    <h2 className='user-page__container__titles-content__fav upper p25 m0' onClick={() => switchUserList('fav')}><span>Skins liked</span></h2>

                    <div className='relative mtbauto'>
                        <Link to='/skins' className="cta">
                            <span>
                                See all skins
                            </span>
                        </Link>
                    </div>
                </div>
                <div className='user-page__container__contents flex-row flex-around h10'>
                    <div className="user-page__container__content__creations w50 h90">
                        <SkinListInput SkinsIn={SKINS} DomClass={"crea-skin-user"} />
                    </div>
                    <div className="user-page__container__content__favorites w50 h90">
                        <SkinListInput SkinsIn={skinfaved} DomClass={"fav-skin-user"} />

                    </div>
                </div>

            </div>
        </div>
    );
}

export default UserProfile;
