import React, { FunctionComponent } from "react";
import Skin from "../../models/skin";

import "./style.css"

type Props = {
    skin: Skin
    exept: Array<number>
    clickcard: any
};

const SkinCard: FunctionComponent<Props> = ({ skin, exept, clickcard }) => {


    const handleSelectSkin = () => {
        clickcard(skin);
      };


    const toogleFavorite = (id: number, type:string) => {
        if (type === "add") {
            console.log("adding favorite");
            skin.favorite+=1 //! patch when back done
        }
        else if (type === "remove") {
            console.log("removing favorite");
            skin.favorite-=1 //! patch when back done
        }
    }


    if (!exept.includes(skin.id)) {

        return (
            <li key={skin.id} className='card-skin flex-row flex-center-y p15 relative'>
                <img alt='skin' className="card-skin__item" src={skin.picture} onClick={handleSelectSkin}></img>
                {skin.favorite > 0 ? (
                    <div className='card-skin__favorite absolute b5 r5 flex-center user-favorite'>
                        <div>{skin.favorite}</div>
                        <i className='material-icons' onClick={() => toogleFavorite(skin.id, 'remove')}>favorite_border</i>
                    </div>
                ) : (

                    <div className='card-skin__favorite absolute b5 r5 flex-center'>
                        <div>{skin.favorite}</div>
                        <i className='material-icons' onClick={() => toogleFavorite(skin.id, 'add')}>favorite_border</i>
                    </div>
                )}
            </li>
        );
    }
    else {
        return (
            <div className="none"></div>
        );
    }

}

export default SkinCard;
