import React, { FunctionComponent } from 'react';
import { Link, useHistory } from 'react-router-dom';
import SkinForm from '../components/skin-form';
import Skin from '../models/skin';
import User from '../models/user';
import skinCreate from '../api-request/skin/create';
import Cookies from 'js-cookie';
import userUpdateCrea from '../api-request/user/update-crea';

type props = {
    user: User
    clickCrea: Function
}

const SkinAdd: FunctionComponent<props> = ({ user, clickCrea }) => {

    const history = useHistory();

    const requestAdd = (newSkinPart: any) => {
        console.log(newSkinPart)
        const newSkin: Skin = { ...newSkinPart, favorite: 0, creator: user._id }

        const token = Cookies.get('token')
        skinCreate(newSkin.classe, newSkin.picture, newSkin.color, newSkin.object, newSkin.favorite, user._id, token).then(result => {

            delete result.__v
            var new_array_crea: Array<string> = user.skinOwner
            new_array_crea.push(result._id)
            userUpdateCrea(new_array_crea, token).then(() => {
                clickCrea(user._id, new_array_crea, result)
                history.push(`user-profile/${user._id}`)
            })
        })
    }

    return (
        <div className='skin-add p50 flex-row flex-center relative'>

            <div className='skin-add__back-button absolute t0 l0 m50'>
                <Link to='/skins' className='cta '>
                    <span>
                        back
                    </span>
                </Link>
            </div>

            <p>{user.pseudo}</p>

            <SkinForm request={requestAdd} />
        </div>
    )
}

export default SkinAdd;
