import React, { FunctionComponent, useState } from "react";
import Skin from "../../models/skin";
import { useHistory } from "react-router-dom";

import "./style.css"
import SKINS from "../../models/mock-skins";


type Props = {
    skin: Skin
};

const SkinDetail: FunctionComponent<Props> = ({ skin }) => {



    return (
        <div className='detail-skin flex-row flex-center'>
            {skin ? (
                <div className='flex-row w80 flex-center-y'>

                    <div className='flex-col'>
                        <p>{skin.classe}</p>
                        <img src={skin.picture}></img>
                    </div>


                    <div className='flex-row flex-around pt25 pb25 w30'>
                        <div>
                            {skin.color.map(color => (
                                <div className="flex-row flex-center-x" key={color}>
                                    <div className="pastille mr10" style={{ background: color }}></div>
                                    <p>{color}</p>
                                </div>
                            ))}
                        </div>
                        <div>
                            {skin.object.map(obj => (
                                <p key={obj}>{obj}</p>
                            ))}
                        </div>

                    </div>

                </div>

            ) : (
                <h2>Aucuns Skin trouv√©</h2>
            )}
        </div>
    )
}
export default SkinDetail;
