import React, { FunctionComponent, useState } from 'react';
import { Link, RouteComponentProps } from 'react-router-dom';
import User from '../models/user';
import voiduser from '../models/void-user';
import Cookies from 'js-cookie';
import getCurrent from '../api-request/user/get-current';


interface Props extends RouteComponentProps<{ id: string }> {
}

const UserSettings: FunctionComponent<Props> = ({ match }) => {

    const [EditPseudo, setEditPseudo] = useState(true);
    const [EditMail, setEditMail] = useState(true);
    const [EditPassWord, setEditPassWord] = useState(true);
    
    const [user, setUser] = useState<User>(voiduser);

  

    return (
        <div className='user-settings p50'>

            <div className='relative mtbauto'>
                <Link to={`/user-profile/${match.params.id}`} className="cta mb35">
                    <span>
                        Back to Profile
                    </span>
                </Link>
            </div>

            <div className="user-settings__container w75">
                <div className="user-settings__container__content">
                    <div className="user-settings__container__content__pseudo">
                        {EditPseudo ? (
                            <div className="user-settings__container__content__pseudo__title">
                                <p>{user.pseudo}</p>
                            </div>
                        ) : (
                            <div className="user-settings__container__content__pseudo__title">
                                <p className='bold blue'>New pseudo :</p>
                                <input type="text" placeholder={user.pseudo} />
                            </div>
                        )}
                    </div>
                    <div className="user-settings__container__content__mail">
                        {EditMail ? (
                            <div className="user-settings__container__content__mail__title">
                                <p>{user.email}</p>
                            </div>
                        ) : (
                            <div className="user-settings__container__content__mail__title">
                                <p className='bold blue'>New mail :</p>
                                <input type="text" placeholder={user.email} />
                            </div>
                        )}
                    </div>
                    <div className="user-settings__container__content__password">
                        {EditPassWord ? (
                            <div className="user-settings__container__content__password__title">
                                <p>{user.password}</p>
                            </div>
                        ) : (
                            <div className="user-settings__container__content__password__title">
                                <p className='bold blue'>New password :</p>
                                <input type="password"/>
                                <p className='bold blue'>Confirm new password :</p>
                                <input type="password"/>
                            </div>
                        )}
                    </div>
                </div>

            </div>


        </div>
    );
}

export default UserSettings;
