import React, { FunctionComponent, useState, useEffect } from 'react';
import { RouteComponentProps, Link } from 'react-router-dom';
import Skin from '../models/skin';
import SKINS from '../models/mock-skins';
import formatDate from '../helpers/format-date';

type Params = { id: string };

const SkinDetail: FunctionComponent<RouteComponentProps<Params>> = ({ match }) => {

    const [skin, setSkin] = useState<Skin | null>(null);

    const type = ["peau", "cheveux", "habit 1", "habit 2", "habit 3"]


    useEffect(() => {
        SKINS.forEach(skin => {
            if (match.params.id === skin.id.toString()) {
                setSkin(skin);
            }
        })
    }, [match.params.id]);

    const [test, setTest] = useState<boolean | null>(null);
    function pipicaca(popo: any) {
        (test == null) ? setTest(popo) : setTest(null)
    }

    return (
        <div className='detail-skin p50 flex-row flex-center-y flex-center-x relative'>
            <div className='absolute t0 l0 m50'>
                <Link to="/skins" className='cta '>
                    <span>
                        back
                    </span>
                </Link>
            </div>
            {skin ? (


                <div className='flex-col w80 flex-center-x flex-center-y'>

                    <h2 className='upper'>{skin.classe}</h2>
                    <div className='flex-row w80'>



                        <div className='flex-row flex-bet p25 w100'>

                            <div>
                                {skin.color.map(color => (
                                    <div className="flex-row flex-center-x flex-end-y g10 " key={color}>
                                        <p>{type[skin.color.indexOf(color)]}</p>
                                        <div className="pastille" style={{ background: color }}></div>
                                        <p>{color}</p>
                                    </div>
                                ))}
                            </div>


                            <div className='flex-col hidden'>
                                <img src={skin.picture} onClick={pipicaca}></img>
                                <p>{formatDate(skin.created)}</p>
                            </div>


                            <div>
                                <h3>Objects : </h3>
                                {skin.object.map(obj => (
                                    <p key={obj}>{obj}</p>
                                ))}
                            </div>

                        </div>
                    </div>

                </div>

            ) : (
                <h2>Aucuns Skin trouv√©</h2>
            )}
        </div>
    )
}

export default SkinDetail;
