import React, { FunctionComponent, useEffect, useState, } from 'react';
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import SkinsList from './pages/skin-list';
import SkinAdd from './pages/skin-add';
import Home from './pages/home';
import Logo from './components/logo-grid/index';
import PageNotFound from './pages/not-found';
import UserHeader from './components/user-header';
import openUserSign from './helpers/display-user';
import UserProfile from './pages/user-page';
import Cookies from 'js-cookie';
import logOut from "./api-request/user/log-out"
import voidskin from './models/void-skin';
import getSkinsList from './api-request/skin/get-list'
import { AnyARecord } from 'dns';

const App: FunctionComponent = () => {

    const [isLog, setIsLog] = useState<String | Boolean>(false);


    const [skins, setSkin] = useState<any | Array<any>>([voidskin]);
    useEffect(() => {
        getSkinsList.then(result => setSkin(result[0]))
    }, [])

    
    const team: any = []
    skins.forEach((skin:any) => {
        if (skin._id === "644ea7dd085fce53b1a010b8" || skin._id === "21" || skin._id === "25" || skin._id === "17") {
            team.push(skin)
        }
    })

    const checklog = () => {
        const check = Cookies.get('token')
        if (check) {
            setIsLog(check)
        }
    }

    const log_out = () => {
        Cookies.remove('token')
        logOut()
        setIsLog(false)
    };

    const clickfav = (newskin_id: any) => {
        setSkin([])
    }

    return (
        <Router>
            <div>
                <nav className="header flex-bet flex-row flex-center-x flex-center-y header w100 zi9 active-in">

                    <Link to="/">
                        <Logo sizeblocs={0.5} hover={false} />
                    </Link>
                    {isLog ? (

                        <div className="header__user-part flex-row w20 w60-tab flex-center g25">
                            <h3 className='m0'>pseudo</h3>
                            <div className='cta cta-red' onClick={() => log_out()}>
                                <span>
                                    Log Out
                                </span>
                            </div>
                        </div>
                    ) : (

                        <div className="header__user-part flex-row w20 w60-tab flex-bet">
                            <div className='cta' onClick={() => openUserSign("in")}>
                                <span>
                                    Sign In
                                </span>
                            </div>
                            <div className='cta' onClick={() => openUserSign("up")}>
                                <span>
                                    Sign Up
                                </span>
                            </div>
                        </div>
                    )}
                </nav>
                <div className="header__marge"></div>
                <Switch>
                    <Route exact path="/" render={() => <Home skinlist={team}  clickfav={clickfav}/>}/>
                    <Route exact path="/skins" render={() => <SkinsList skinlist={skins} clickfav={clickfav}/>}/>
                    <Route exact path="/skins/add" render={() => <UserProfile skinlist={skins} />}/>
                    <Route exact path="/user-profile" component={SkinAdd}></Route>
                    <Route component={PageNotFound}></Route>
                </Switch>

            </div>
            <UserHeader testmaxleroux={checklog} />

        </Router>

    )
}

export default App;
