import React, { FunctionComponent, useState, useEffect } from 'react';
import { RouteComponentProps } from 'react-router-dom';
import SkinForm from '../components/skin-form';
import Skin from '../models/skin';
import SKINS from '../models/mock-skins';
 
type Params = { id: string };
  
const SkinEdit: FunctionComponent<RouteComponentProps<Params>> = ({ match }) => {
    
  const [skin, setSkin] = useState<Skin|null>(null);
  
  useEffect(() => {
    SKINS.forEach(skin => {
      if (match.params.id === skin.id.toString()) {
        setSkin(skin);
      }
    })
  }, [match.params.id]);
    
  return (
    <div className='w100 flex-center'>
      { skin ? (
        <div className="flex-col w80 mt50 flex-center">
            <h2 className=" mb50">Éditer { skin.classe }</h2>
            <SkinForm skin={skin}></SkinForm>
        </div>
      ) : (
        <h4 className="center">Aucun skin à afficher !</h4>
      )}
    </div>
  );
}
  
export default SkinEdit;
