import React, { FunctionComponent, useEffect, useState, } from 'react';
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import SkinsList from './pages/skin-list';
import SkinAdd from './pages/skin-add';
import Home from './pages/home';
import Logo from './components/logo-grid/index';
import PageNotFound from './pages/not-found';
import UserHeader from './components/user-header';
import openUserSign from './helpers/display-user';
import UserProfile from './pages/user-page';
import Cookies from 'js-cookie';
import logOut from "./api-request/user/log-out"
import voidskin from './models/void-skin';
import getSkinsList from './api-request/skin/get-list'
import getUsersList from './api-request/user/get-list'
import User from './models/user';
import Skin from './models/skin';
import voiduser from './models/void-user';
import getCurrent from './api-request/user/get-current';
import UserSettings from './pages/user-settings';
import DeleteUserById from './api-request/user/delete';
import userUpdate from './api-request/user/update';

const App: FunctionComponent = () => {


    const [isLog, setIsLog] = useState<String | Boolean>(false);
    const [currentUser, setCurrentUser] = useState<User>(voiduser);
    const [skins, setSkins] = useState<Array<Skin>>([voidskin]);
    const [users, setUsers] = useState<Array<User>>([voiduser]);
    const [userLiked, setUserLiked] = useState<Array<string>>([""])

    useEffect(() => {
        getSkinsList.then(result => {
            setSkins(result[0])
        })
        getUsersList.then(result => {
            setUsers(result[0])
        })
    }, [])

    const checklog = () => {
        const token = Cookies.get('token')
        if (token) {
            setIsLog(token)
        }
    }

    useEffect(() => {
        const token = Cookies.get('token')
        getCurrent(token).then(result => {
            setCurrentUser(result[0])
            setUserLiked(result[0].skinFavorite)


        })
    }, [isLog])

    const log_out = () => {
        Cookies.remove('token')
        logOut().then(result => {
            setIsLog(false)
            setUserLiked([])
            setCurrentUser(voiduser)
        })

    };

    const clickfav = (skin_id: any, user_id: any, new_fav: number, new_array_fav: Array<string>) => {
        var indexSkin = skins.findIndex((obj: any) => obj._id === skin_id);
        if (indexSkin !== -1) {
            skins[indexSkin] = { ...skins[indexSkin], favorite: new_fav };
        }
        var indexUser = users.findIndex((obj: any) => obj._id === user_id);
        if (indexUser !== -1) {
            users[indexUser] = { ...users[indexUser], skinFavorite: new_array_fav };
        }
        setSkins(skins)
        setUsers(users)
        setUserLiked(new_array_fav)
    }

    const clickCrea = (user_id: string, new_array_crea: Array<string>, result: Skin) => {
        var indexUser = users.findIndex((obj: any) => obj._id === user_id);
        if (indexUser !== -1) {
            users[indexUser] = { ...users[indexUser], skinOwner: new_array_crea };
        }
        setSkins(skins)
        setUsers(users)
    }


    const updateUser = (name: string, value: string) => {
        
        const token = Cookies.get('token')
        if (token) {
        userUpdate(currentUser, token, name, value)
        }
    }
    const DeleteUser = (target: string) => {
        if (target === 'me') {
            const token = Cookies.get('token')
            if (token) {
                DeleteUserById(token).then(() => {
                    console.log('ok')
                    log_out()
                })
            }
        } else {
            console.log('delete user')
        }
    }


    const team: any = []
    skins.forEach((skin: any) => {
        if (skin._id === "644ea7dd085fce53b1a010b8" || skin._id === "21" || skin._id === "25" || skin._id === "17") {
            team.push(skin)
        }
    })

    return (
        <Router>
            <div>
                <nav className="header flex-bet flex-row flex-center-x flex-center-y header w100 zi9 active-in">

                    <Link to="/">
                        <Logo sizeblocs={0.5} hover={false} />
                    </Link>
                    {isLog ? (

                        <div className="header__user-part flex-row w20 w60-tab flex-center g25">
                            <h3 className='m0 blue'>
                                <Link to={`/user-profile/${currentUser._id}`} >
                                    {currentUser.pseudo}
                                </Link>
                            </h3>
                            <div className='cta cta-red' onClick={() => log_out()}>
                                <span>
                                    Log Out
                                </span>
                            </div>
                        </div>
                    ) : (

                        <div className="header__user-part flex-row w20 w60-tab flex-bet">
                            <div className='cta' onClick={() => openUserSign("in")}>
                                <span>
                                    Sign In
                                </span>
                            </div>
                            <div className='cta' onClick={() => openUserSign("up")}>
                                <span>
                                    Sign Up
                                </span>
                            </div>
                        </div>
                    )}
                </nav>
                <div className="header__marge"></div>
                <Switch>
                    <Route exact path="/" render={() => <Home skinlist={team} clickfav={clickfav} CurrentUserLike={userLiked} />} />
                    <Route exact path="/skins" render={() => <SkinsList skinlist={skins} clickfav={clickfav} CurrentUserLike={userLiked} />} />
                    <Route path="/user-profile/:id" render={(props) => <UserProfile {...props} skinList={skins} userList={users} clickfav={clickfav} CurrentUserLike={userLiked} />} />
                    <Route exact path="/user-settings/:id" render={(props) => <UserSettings {...props} usercalled={currentUser} requestUpdate={updateUser} requestDelete={DeleteUser}/>} />
                    <Route exact path="/skins/add" render={() => <SkinAdd user={currentUser} clickCrea={clickCrea} />}></Route>
                    <Route component={PageNotFound}></Route>
                </Switch>

            </div>
            <UserHeader testmaxleroux={checklog} />

        </Router>

    )
}

export default App;
