import React, { FunctionComponent } from 'react';
import { Link } from 'react-router-dom';
import SkinForm from '../components/skin-form';
import Skin from '../models/skin';
import User from '../models/user';
import skinCreate from '../api-request/skin/create';
import Cookies from 'js-cookie';

type props = {
    user: User
}

const SkinAdd: FunctionComponent<props> = ({ user }) => {

    const requestAdd = (newSkin: any) => {
        console.log(newSkin)
        const token = Cookies.get('token')
        skinCreate(newSkin.classe, newSkin.picture, newSkin.color, newSkin.object, 0, user._id, token).then(result => {
            console.log('ok')
        })
    }

    return (
        <div className='skin-add p50 flex-row flex-center relative'>

            <div className='skin-add__back-button absolute t0 l0 m50'>
                <Link to='/skins' className='cta '>
                    <span>
                        back
                    </span>
                </Link>
            </div>

            <p>{user.pseudo}</p>

            <SkinForm request={requestAdd} />
        </div>
    )
}

export default SkinAdd;
