import React, { FunctionComponent, useEffect, useState } from "react";
import Skin from "../../models/skin";
import "./style.css"

type Props = {
    skin: Skin
    exept: Array<string>
    clickcard: any
    update: Function | any
};

const SkinCard: FunctionComponent<Props> = ({ skin, exept, clickcard, update }) => {

    
    var [countfav, setCountfav] = useState(skin.favorite);

    const handleSelectSkin = () => {
        clickcard(skin);
      };


    const toogleFavorite = (id: string, type:string) => {
        if (type === "add") {
            console.log("adding favorite");
            countfav +=1
            setCountfav(countfav) //! patch when back done
        }
        else if (type === "remove") {
            console.log("removing favorite");
            countfav -=1
            setCountfav(countfav) //! patch when back done
        }
    }


    if (!exept.includes(skin._id)) {

        return (
            <li key={skin._id} className='card-skin flex-row flex-center-y p15 relative'>
                <img alt='skin' className="card-skin__item" src={skin.picture} onClick={handleSelectSkin}></img>
                {countfav > 0 ? (
                    <div className='card-skin__favorite absolute b5 r5 flex-center user-favorite'>
                        <div>{countfav}</div>
                        <i className='material-icons' onClick={() => toogleFavorite(skin._id, 'remove')}>favorite_border</i>
                    </div>
                ) : (

                    <div className='card-skin__favorite absolute b5 r5 flex-center'>
                        <div>{countfav}</div>
                        <i className='material-icons' onClick={() => toogleFavorite(skin._id, 'add')}>favorite_border</i>
                    </div>
                )}
            </li>
        );
    }
    else {
        return (
            <div className="none"></div>
        );
    }

}

export default SkinCard;
