import React, { FunctionComponent, useState, useEffect } from 'react';
import { RouteComponentProps, Link } from 'react-router-dom';
import Skin from '../models/skin';
import SKINS from '../models/mock-skins';

type Params = { id: string };

const SkinDetail: FunctionComponent<RouteComponentProps<Params>> = ({ match }) => {

    const [skin, setSkin] = useState<Skin | null>(null);

    const type = ['peau', 'cheveux', 'habit 1', 'habit 2', 'habit 3', 'test']


    useEffect(() => {
        SKINS.forEach(skin => {
            if (match.params.id === skin.id.toString()) {
                setSkin(skin);
            }
        })
    }, [match.params.id]);

    const [test, setTest] = useState<boolean | null>(null);
    function pipicaca(popo: any) {
        (test == null) ? setTest(popo) : setTest(null)
    }

    handleClick = () => {
        console.log('this vaut :', this);
      }

    return (
        <div className='detail-skin p50 flex-row flex-center relative'>
            <div className='detail-skin__back-button absolute t0 l0 m50'>
                <Link to='/skins' className='cta '>
                    <span>
                        back
                    </span>
                </Link>
            </div>
            {skin ? (


                <div className='detail-skin__container flex-col w80 flex-center-x flex-center-y'>

                    <h2 className='detail-skin__title upper'>{skin.classe}</h2>




                    <div className='detail-skin__content flex-row flex-bet p25 w80'>

                        <div className='detail-skin__bloc-colors g10 flex-col'>
                            <h3 className='detail-skin__bloc-colors__title'>Colors : </h3>
                            {skin.color.map(color => (
                                <div className='detail-skin__bloc-colors__color flex-row flex-center-x flex-end-y g10 ' key={color} onClick={this.handleClick}>
                                    <p className='detail-skin__bloc-colors__color__info-value'>{type[skin.color.indexOf(color)]}</p>
                                    <div className='detail-skin__bloc-colors__color__pastille' style={{ background: color }}></div>
                                    <p className='detail-skin__bloc-colors__color__value'>{color}</p>
                                </div>
                            ))}
                        </div>


                        <div className='detail-skin__bloc-img'>
                            <img src={skin.picture} onClick={pipicaca}></img>
                        </div>


                        <div className='detail-skin__bloc-object g10 flex-col' >
                            <h3 className='detail-skin__bloc-object__title'>Objects : </h3>
                            {skin.object.map(obj => (
                                <div className='detail-skin__bloc-objects__object g10 flex-col'>
                                    <p className='detail-skin__bloc-object__object__value' key={obj}>{obj}</p>
                                </div>
                            ))}
                        </div>

                    </div>

                </div>

            ) : (
                <div className='detail-skin__not-found'>
                    <h2 className='detail-skin__not-found__title'>No skin find</h2>
                </div>
            )}
        </div>
    )
}

export default SkinDetail;
