import React, { FunctionComponent } from 'react';
import SkinList from './skin-list';

const UserProfile: FunctionComponent = () => {

    const switchUserList = (type:string) => {
        if (type === 'fav') {
            if (!document.querySelector('.user-page__container__contents')!.classList.value.includes(type) ) {
                document.querySelector('.user-page__container__contents')!.classList.remove('crea');
                document.querySelector('.user-page__container__contents')!.classList.add('fav');
            }
        }
        
        if (type === 'crea') {
            if (!document.querySelector('.user-page__container__contents')!.classList.value.includes(type) ) {
                document.querySelector('.user-page__container__contents')!.classList.add('crea');
                document.querySelector('.user-page__container__contents')!.classList.remove('fav');
            }
        }
    }


    return (
        <div className='user-page flex-start-x flex-center-y flex-row p50 pb0 relative'>
            <div className="user-page__user-info flex-col w25">
                <div className='user-page__user-info__container flex-center flex-col'>
                    <img src="https://media.discordapp.net/attachments/967347575390863360/1097526768572846110/pict_profile.png?width=671&height=671" alt="" width={"50%"} height={"50%"} />
                    <h2>pseudo</h2>
                </div>
            </div>
            <div className="user-page__container flex-col w75 flex-start-x flex-start-y" >


                <div className='user-page__container__titles-content flex-row w100 flex-around h10 relative'>
                    <h2 className='upper' onClick={() => switchUserList('crea')}><span>Skins creations</span></h2>
                    <h2 className='upper' onClick={() => switchUserList('fav')}><span>Skins liked</span></h2>
                </div>
                <div className='user-page__container__contents flex-row flex-around h10 crea'>
                    <div className="user-page__container__content__creations w50 h90">
                        <SkinList />
                    </div>
                    <div className="user-page__container__content__favorites w50 h90">
                        <SkinList />
                    </div>
                </div>

            </div>
        </div>
    );
}

export default UserProfile;
