import React, { FunctionComponent, useState } from "react";
import Event from "../../models/event";

import "./style.css"
import voidevent from "../../models/void-event";
import { Link } from "react-router-dom";
import Cookies from "js-cookie";
import userUpdateFav from "../../api-request/user/update-fav";
import eventUpdate from "../../api-request/event/update";
import getUserById from "../../api-request/user/get-by-id";
import User from "../../models/user";

type Props = {
    event: Event
    clickcard: Function
    CurrentUser: User
};

const EventDetail: FunctionComponent<Props> = ({ event, clickcard, CurrentUser }) => {


    var [user, setUser] = useState(CurrentUser);
    var [step, setStep] = useState<number>(0);


    const handleSelectEvent = () => {
        clickcard(voidevent);
    };

    return (
        <div className='detail-event p50 flex-row flex-center flex-col-tab flex-start-y-tab'>
            <div className='detail-event__back-button absolute t0 l0 m50'>
                <div className='cta' onClick={handleSelectEvent}>
                    <span>
                        back
                    </span>
                </div>
            </div>


            {event ? (
                <div className='detail-event__container flex-col w80 flex-center-x flex-center-y w95-tab t0'>
                    {(step >= 0) ? (

                        <div>
                            <div className="flex-row flex-center g15">
                                <img src={event.picture} alt="" />
                                <div className="flex-col g15">
                                    <h2>{event.name}</h2>
                                    <p>{event.date}</p>
                                    <p>{event.place}</p>
                                </div>
                            </div>
                            <div className="flex-col mt25">
                                <p>{event.dispoPlace === 0 ? (<span>no</span>) : (<span>{event.dispoPlace}</span>)} spots left</p>
                                 <p>{event.cost} $</p>
                            </div>
                        </div>
                    ) : null}

                    {(step >= 1) ? (

                        <div>
                            <div className="flex-row flex-center g15">
                                {CurrentUser.payement ? (
                                    <div>
                                        <p>Payement informations are valids</p>
                                    </div>
                                ) : (
                                    <div>pasok</div>
                                )}
                            </div>
                            <div className="flex-row g50 mt25">
                            </div>
                        </div>
                    ) : null}
                    <div className="flex-rox flex-bet g50">
                        {step >= 1 ? (
                            <div className="cta" onClick={() => setStep(step - 1)}>
                                <span>previous step</span>
                            </div>
                        ) : (
                            <div className="cta cta-grey">
                                <span>previous step</span>
                            </div>
                        )}
                        {true && event.dispoPlace !== 0 ? (
                            <div className="cta" onClick={() => setStep(step + 1)}>
                                <span>next step</span>
                            </div>
                        ) : null}
                    </div>

                </div>
            ) : (
                <div className='detail-event__not-found'>
                    <h2 className='detail-event__not-found__title'>No event find</h2>
                </div>
            )}
        </div>
    )
}

export default EventDetail;
